mapper.update:
UPDATE users
SET ustat = '1'
WHERE uid = #{uid}

UPDATE users
SET ustat = '0'
WHERE uid = #{uid}

UPDATE users
SET umail = #{umail}
WHERE uid = #{uid} AND upass = #{upass}

UPDATE users
SET upass = #{newpass}
WHERE uid = #{uid} AND upass = #{oldpass}

UPDATE users
SET uname = #{uname}, umail = #{umail}, uacce = #{uacce}, ustat = #{ustat}
WHERE uid = #{uid}
